import gsap from "gsap";
import { onMounted } from "vue";
export default {
  __name: 'resettip',
  setup(__props, {
    expose: __expose
  }) {
    let circles; //所有的圆环DOM元素
    // 重置：短暂显示圆环，并消失
    function reset() {
      gsap.fromTo(circles, {
        scale: 0,
        opacity: 1
      }, {
        scale: 1,
        opacity: 0,
        duration: 1,
        stagger: 0.1,
        ease: "power3.inOut"
      });
    }
    // 将重置函数提供给stage
    __expose({
      reset
    });
    onMounted(() => {
      circles = document.querySelectorAll(".resettip_circle");
    });
    const __returned__ = {
      get circles() {
        return circles;
      },
      set circles(v) {
        circles = v;
      },
      reset,
      get gsap() {
        return gsap;
      },
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
